import React, {useState} from "react";
import { Link } from "react-router-dom";
import { IconArrowNarrowRight } from '@tabler/icons-react';

const Signup = () => {
    const [formData, setFormData] = useState({
        name: "",
        email: "",
        phone: "",
        password: "",
    });

    const [errors, setErrors] = useState("");

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!formData.name || !formData.email || !formData.phone || !formData.password) {
            setErrors("All fields are required.");
            return;
        }
        setErrors("");
        console.log("Form submitted:", formData);
        // Proceed with form submission (API call or other logic)
    };

    return(
        <form className="box-container register-container" >
            <div className="form-title">
                <span>Welcome Back</span>
                <p>Please Enter your Account details</p>
            </div>
            {/* <div className="error-message">Error Message</div> */}
            {errors && <div className="error-message">{errors}</div>}
            <div className="form-inputs">
                <div className="input-box">
                    <input type="text" name="name" className="inputs input-field" placeholder="Name" value={formData.name} onChange={handleChange} />
                </div>
                <div className="input-box">
                    <input type="email" name="email" className="inputs input-field" placeholder="Email" value={formData.email} onChange={handleChange} />
                </div>
                <div className="input-box">
                    <input type="tel" name="phone" className="inputs input-field" placeholder="Phone" value={formData.phone} onChange={handleChange} />
                </div>
                <div className="input-box">
                    <input type="password" name="password" className="inputs input-field" placeholder="Password" value={formData.password} onChange={handleChange} />
                </div>
            </div>
            <div className="input-box">
                <button type="submit" className="inputs btn submit-btn">
                    <span>Sign Up</span>
                    <IconArrowNarrowRight size={20} />
                </button>
            </div>
            <div className="form-bottom-links">
                <span>Already have an account? <Link to="/signin" className="bottom-link">Sign In</Link></span>
            </div>
        </form>
    )
}

export default Signup;
