import React, { useState } from "react";
import { Link } from "react-router-dom";
import { IconArrowNarrowRight } from '@tabler/icons-react';

const Signin = () => {
    const [formData, setFormData] = useState({ name: "", email: "", phone: "", password: "" });
    const [errors, setErrors] = useState({});

    // Validation Function
    const validate = () => {
        let newErrors = {};

        // Name Validation (Only letters and spaces, min 3 characters)
        if (!formData.name.trim()) {
            newErrors.name = "Name is required.";
        } else if (!/^[A-Za-z\s]{3,}$/.test(formData.name)) {
            newErrors.name = "Enter a valid name (min 3 letters, only alphabets).";
        }

        // Email Validation
        if (!formData.email.trim()) {
            newErrors.email = "Email is required.";
        } else if (!/^\S+@\S+\.\S+$/.test(formData.email)) {
            newErrors.email = "Enter a valid email.";
        }

        // Phone Number Validation (Only 10 digits)
        if (!formData.phone.trim()) {
            newErrors.phone = "Phone number is required.";
        } else if (!/^[6-9]\d{9}$/.test(formData.phone)) {
            newErrors.phone = "Enter a valid 10-digit phone number.";
        }

        // Password Validation (Min 6 characters, at least 1 letter and 1 number)
        if (!formData.password.trim()) {
            newErrors.password = "Password is required.";
        } else if (!/^(?=.*[A-Za-z])(?=.*\d)[A-Za-z\d]{6,}$/.test(formData.password)) {
            newErrors.password = "Password must be at least 6 characters long and contain at least 1 letter and 1 number.";
        }

        setErrors(newErrors);
        return Object.keys(newErrors).length === 0; // Returns true if no errors
    };

    // Handle Input Change
    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    // Handle Form Submission
    const handleSubmit = (e) => {
        e.preventDefault();
        if (validate()) {
            console.log("Form Submitted:", formData);
            alert("Form Submitted Successfully!");
        }
    };

    return (
        <form className="box-container login-container" onSubmit={handleSubmit}>
            <div className="form-title">
                <span>Welcome Back</span>
                <p>Please enter your account details</p>
            </div>

            <div className="form-inputs">
                {/* Name Input */}
                <div className="input-box">
                    <label htmlFor="name">Full Name</label>
                    <input 
                        type="text" 
                        id="name" 
                        name="name" 
                        className="inputs input-field" 
                        placeholder="Enter your full name"
                        value={formData.name}
                        onChange={handleChange}
                    />
                    {errors.name && <p className="error-message">{errors.name}</p>}
                </div>

                {/* Email Input */}
                <div className="input-box">
                    <label htmlFor="email">Email</label>
                    <input 
                        type="email" 
                        id="email" 
                        name="email" 
                        className="inputs input-field" 
                        placeholder="Enter your email"
                        value={formData.email}
                        onChange={handleChange}
                    />
                    {errors.email && <p className="error-message">{errors.email}</p>}
                </div>

                {/* Phone Number Input */}
                <div className="input-box">
                    <label htmlFor="phone">Phone Number</label>
                    <input 
                        type="tel" 
                        id="phone" 
                        name="phone" 
                        className="inputs input-field" 
                        placeholder="Enter your phone number"
                        value={formData.phone}
                        onChange={handleChange}
                    />
                    {errors.phone && <p className="error-message">{errors.phone}</p>}
                </div>

                {/* Password Input */}
                <div className="input-box">
                    <label htmlFor="password">Password</label>
                    <input 
                        type="password" 
                        id="password" 
                        name="password" 
                        className="inputs input-field" 
                        placeholder="Enter your password"
                        value={formData.password}
                        onChange={handleChange}
                    />
                    {errors.password && <p className="error-message">{errors.password}</p>}
                </div>
            </div>

            <div className="forgot-pass">
                <Link to="/forgot-password">Forgot password?</Link>
            </div>

            <div className="input-box">
                <button type="submit" className="inputs btn submit-btn">
                    <span>Sign In</span>
                    <IconArrowNarrowRight size={20} />
                </button>
            </div>

            <div className="form-bottom-links">
                <span>Don't have an account? <Link to="/signup" className="bottom-link">Sign Up</Link></span>
            </div>
        </form>
    );
};

export default Signin;
